class InputItem extends React.Component {

	render() {
		var the_name = this.props.list_type + " " + (this.props.index + 1).toString();
		return <div className="field">
			<input type="text" placeholder={the_name} onChange={this.props.onChange} />
		</div>;
	}
}

export default class NewMemberView extends React.Component {
	
	constructor(props) {
		super(props);
		this.state = {
			[NAME]: '',
			[ID]: '',
			[INTEREST]: [],
			[SKILL]: [],
			[CERT]: []
		}
	}

	addToCount(count) {
		this.setState({
			[count]: this.state[count].concat('')
		});
	}
	
	removeFromCount(count) {
		this.setState({
			[count]: this.state[count].splice(0, (this.state[count].length - 1))
		});
	}

	update(item, index, e) {
		switch (item) {
			case 'id':
				this.setState({ [ID]: e.target.value });
				break;
			case 'name':
				this.setState({ [NAME]: e.target.value });
				break;
			default:
				var new_state = this.state[item];
				new_state[index] = e.target.value;
				this.setState({ [item]: new_state });
		}
	}

	submit() {
		console.log(this.state[ID]);
		console.log(this.state[NAME]);
		console.log(this.state[INTEREST]);
		console.log(this.state[SKILL]);
		console.log(this.state[CERT]);
		var data = {
			id: this.state[ID],
			name: this.state[NAME],
			interests: this.state[INTEREST],
			skills: this.state[SKILL],
			certs: this.state[CERT]
		};
		API.newMember(data);
	}



	render() {
		
		var interest_list = [];
		this.state[INTEREST].forEach(function(value, index) {
			interest_list.push(<InputItem index={index} key={index} onChange={this.update.bind(this, INTEREST, index)} list_type={INTEREST} />)
		}.bind(this));
		
		var skill_list = [];
		this.state[SKILL].map(function(num, index) {
			skill_list.push(<InputItem index={index} key={index} onChange={this.update.bind(this, SKILL, index)} list_type={SKILL} />)
		}.bind(this));
		
		var cert_list = [];	
		this.state[CERT].map(function(num, index) {
			cert_list.push(<InputItem index={index} key={index} onChange={this.update.bind(this, CERT, index)} list_type={CERT} />)
		}.bind(this));
		
		return <div>
			<div className="ui yellow segment">
			<form className="ui form">
				<div className="two fields">
				<div className="required field">
					<label>ID</label>
					<input type="text" name="id" placeholder="ID" onChange={this.update.bind(this, ID, 0)} />
				</div>
				<div className="required field">
					<label>Name</label>
					<input type="text" name="name" placeholder="Name" onChange={this.update.bind(this, NAME, 0)} />
				</div>
				</div>

				{/* interests */}
				<div className="ui left labeled button" tabIndex="0">
					<div className="ui basic label">
						Interests
					</div>
					<div onClick={this.addToCount.bind(this, INTEREST)} className="ui button">
						Add
					</div>
				</div>
				<div onClick={this.removeFromCount.bind(this, INTEREST)} className="negative ui right floated button">
					Remove
				</div>
				<div className="ui hidden divider"></div>
				<div>
					{interest_list}
				</div>
				<div className="ui divider"> </div>

				{/* skills */}
				<div className="ui left labeled button" tabIndex="0">
					<div className="ui basic label">
						Skills
					</div>
					<div onClick={this.addToCount.bind(this, SKILL)} className="ui button">
						Add
					</div>
				</div>
				<div onClick={this.removeFromCount.bind(this, SKILL)} className="negative ui right floated button">
					Remove
				</div>
				<div className="ui hidden divider"></div>
				<div>
					{skill_list}
				</div>
				<div className="ui divider"> </div>

				{/* certifications */}
				<div className="ui left labeled button" tabIndex="0">
					<div className="ui basic label">
						Certifications
					</div>
					<div onClick={this.addToCount.bind(this, CERT)} className="ui button">
						Add
					</div>
				</div>
				<div onClick={this.removeFromCount.bind(this, CERT)} className="negative ui right floated button">
					Remove
				</div>
				<div className="ui hidden divider"></div>
				<div>
					{cert_list}
				</div>
			</form>
			<div className="ui hidden divider"></div>
			<div onClick={this.submit.bind(this)} className="white ui button">
				Submit
			</div>
			</div>
		</div>;
	}
}

import React from 'react';
import ProjectCard from './projectcard';
import Api from '../api';

var API = new Api();

class ProjectListItem extends React.Component {

	constructor(props) {
		super(props);
	}

	clicked() {
		this.props.onClick(this.props.data.id);
	}

	render() {
		var name = this.props.data.name;
		var budget = this.props.data.budget;
		var spent = this.props.data.spent;

		return <tr onClick={this.clicked.bind(this)}>
			<td>{name}</td>
			<td>${budget}</td>
			<td>${spent}</td>
		</tr>;


	}
}

export default class ListProjectView extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			id: false,
			data: API.getMemberList()
		}
	}

	componentDidMount() {
		/*var url = API.url + "/memberlist";
		$.ajaxSetup({async:false});
		$.get(url, function(result) {
			this.setState({data : result});
		}.bind(this));
		$.ajaxSetup({async:true});*/
	}	

	handleClick(item) {
		this.setState({id: item});
	}

	getSelected(id, data) {
		var selected;
		data.forEach(function(data) {
			if (data.id === id) {
				selected = data;
			}
		});
		return selected;
	}

	render() {

		var data = API.getProjectList();
		var rows = [];
		var selected = this.getSelected(this.state.id, data);

		data.forEach(function(data) {
			rows.push(<ProjectListItem onClick={this.handleClick.bind(this)} data={data} key={data.name} />)
		}.bind(this));

		var table = <table className="ui striped yellow table" >
			<thead><tr>
				<th>Project Name</th>
				<th>Budget</th>
				<th>Spent</th>
			</tr></thead>
			<tbody>
				{rows}
			</tbody>
		</table>

		var view;

		if (!this.state.id) {
			view = table
		} else {
			var remaining = (parseFloat(selected.budget) - parseFloat(selected.spent)).toFixed(2);
			var card = <ProjectCard onClick={this.handleClick.bind(this)} id={selected.id} name={selected.name} remaining={remaining}/>
			view = <div className="ui grid">
				<div className="ten wide column">
					{table}
				</div>
				<div className="one wide column">
				</div>
				<div className="four wide column">
					<div style={{textAlign: 'center'}}><div sytle={{display: 'inline-block'}}>{card}</div></div>
				</div>
			</div>;
		}
		
		return <div>
			{view}
		</div>;
	}
}

