}

export default class NewProjectView extends React.Component {
	
	constructor(props) {
		super(props);
		this.state = {
			[NAME]: '',
			[BUDGET]: '',
			[MEMBERS]: []
		}
	}

	addToCount(count) {
		this.setState({
			[count]: this.state[count].concat('')
		});
	}
	
	removeFromCount(count) {
		this.setState({
			[count]: this.state[count].splice(0, (this.state[count].length - 1))
		});
	}

	update(item, index, e) {
		switch (item) {
			case 'budget':
				this.setState({ [BUDGET]: e.target.value });
				break;
			case 'name':
				this.setState({ [NAME]: e.target.value });
				break;
			default:
				var new_state = this.state[item];
				new_state[index] = e.target.value;
				this.setState({ [item]: new_state });
		}
	}

	submit() {
		console.log(this.state[BUDGET]);
		console.log(this.state[NAME]);
		console.log(this.state[MEMBERS]);
		var data = {
			name: this.state[NAME],
			budget: this.state[BUDGET],
			members: this.state[MEMBERS]
		}
		API.newProject(data);
	}



	render() {
		
		var members_list = [];
		this.state[MEMBERS].forEach(function(value, index) {
			members_list.push(<InputItem index={index} key={index} onChange={this.update.bind(this, MEMBERS, index)} list_type={MEMBERS} />)
		}.bind(this));
		
		
		return <div>
			<div className="ui yellow segment">
			<form className="ui form">
				<div className="two fields">
				<div className="required field">
					<label>Name</label>
					<input type="text" name="name" placeholder="Name" onChange={this.update.bind(this, NAME, 0)} />
				</div>
				<div className="required field">
					<label>Budget</label>
					<input type="text" name="budget" placeholder="Budget" onChange={this.update.bind(this, BUDGET, 0)} />
				</div>
				</div>

				{/* members */}
				<div className="ui left labeled button" tabIndex="0">
					<div className="ui basic label">
						Members
					</div>
					<div onClick={this.addToCount.bind(this, MEMBERS)} className="ui button">
						Add
					</div>
				</div>
				<div onClick={this.removeFromCount.bind(this, MEMBERS)} className="negative ui right floated button">
					Remove
				</div>
				<div className="ui hidden divider"></div>
				<div>
					{members_list}
				</div>
				<div className="ui divider"> </div>

			</form>
			<div className="ui hidden divider"></div>
			<div onClick={this.submit.bind(this)} className="white ui button">
				Submit
			</div>
			</div>
		</div>;
	}
}

import React from 'react';
import CountBarChart from './count_bar_chart';


export default class OurMemberView extends React.Component {
	
	constructor(props) {
		super(props);
		this.state = {
			type: 'interests'
		}
	}

	handleUpdate(item) {
		this.setState({type: item});
	}

	getSelected(id, data) {
		var selected;
		data.forEach(function(item) {
			if ((item.id) == (id)) {
				selected = item;
			}
		});
		return selected;
	}

	render() {
		
		var types = ['interest'];
		var view = this.state.type;
		var isActive = v => v === view ? 'active' : '';

		return <div>
			<div className="ui top attached menu">
				<a onClick={this.handleUpdate.bind(this, 'interests')} className={`item ${isActive('interests')}`}>
					Interests	
				</a>
				<a onClick={this.handleUpdate.bind(this, 'skills')} className={`item ${isActive('skills')}`}>
					Skills	
				</a>
				<a onClick={this.handleUpdate.bind(this, 'certifications')} className={`item ${isActive('certifications')}`}>
					Certifications	
				</a>
			</div>
			<div className="ui yellow attached segment">
				<div style={{paddingTop: '10px'}}>
					<CountBarChart type={this.state.type} />
				</div>
			</div>
		</div>
	}
}

import React from 'react';
import VisitLineChart from './visit_line_chart';
import Api from '../api';

var API = new Api();

export default class VisitView extends React.Component {
	
	constructor(props) {
		super(props);
		this.state = {
			type: 'interests'
		}
	}

	componentDidMount() {
		var item = API.getInterestList()[0].interest;
		this.setState({type : item});
	}

	handleUpdate(item) {
		this.setState({type: item});
	}

	getSelected(id, data) {
		var selected;
		data.forEach(function(item) {
			if ((item.id) == (id)) {
				selected = item;
			}
		});
		return selected;
	}

	render() {
		
		var interests = API.getInterestList();
		var items =[]
		
		var view = this.state.type;
		var isActive = v => v === view ? 'active' : '';
		
		interests.forEach(function(data, i) {
			items.push(<a onClick={this.handleUpdate.bind(this, data.interest)} className={`item ${isActive(data.interest)}`} key={i}>{data.interest}</a>)
		}.bind(this));


		return <div>
			<div className="ui yellow segment">
				<div className="ui grid">
					<div className="three wide column">
						<div className="ui vertical pointing menu">
							<div className="item"><h3>I want to learn ...</h3></div>
							{items}
						</div>
					</div>
					<div className="thirteen wide column">
						<VisitLineChart type={this.state.type} />
					</div>
				</div>
			</div>
		</div>
	}
}

import React from 'react';
import MemberCard from './membercard';
import MakerspaceBarChart from './makerspace_bar_chart';
import Api from '../api';

var API = new Api();

class DropdownItem extends React.Component {
	render() {
		var item = this.props.item;

		return <option value={item}>
			{item}
		</option>
	}
}

export default class MakerspaceView extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			id: false
		}
	}

	handleClick(item) {
		this.setState({id: item});
	}

	handleUpdate(type, e) {
		if (type === 'ID') {
			this.setState({id: e.target.value});
		}
	}

	getSelected(id, data) {
		var selected;
		data.forEach(function(item) {
			if ((item.id) == (id)) {
				selected = item;
			}
		});
		return selected;
	}

	render() {

		var data = API.getUsers("current");
		var idrows = [];
		var selected = this.getSelected(this.state.id, data);

		data.forEach(function(data, index) {
			idrows.push(<DropdownItem item={data.id} key={index} />);
		});

		var idDrop = <select value={this.state.id} className="ui fluid search dropdown" id="idDrop" onChange={this.handleUpdate.bind(this, 'ID')}>
			<option value=""></option>
			{idrows}
		</select>

		var card;
		if (selected) {
			var card = <MemberCard onClick={this.handleClick.bind(this)} id={selected.id} name={selected.name} last_visit={selected.last_visit}/>
		}

		var view = <div className="ui grid">
			<div className="four wide column">
				<div className="ui yellow segment">
					<div style={{paddingTop: '20px', paddingBottom: '20px'}} >
						<div style={{paddingBottom: '20px'}} ><h2>Current Makers: {data.length}</h2></div>
						<div>{idDrop}</div>
					</div>
				</div>
				<div style={{textAlign: 'center'}}><div sytle={{display: 'inline-block'}}>{card}</div></div>
			</div>
			<div className="twelve wide column">
				<div className="ui yellow segment">
					<MakerspaceBarChart />
				</div>
			</div>
		</div>;
		
		return <div>
			{view}
		</div>;
	}
}

